import React, { useState, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import { toast } from 'react-toastify';
import axios from 'axios';
import { 
  CloudArrowUpIcon,
  DocumentTextIcon,
  XMarkIcon,
  ArrowLeftIcon,
  MagnifyingGlassIcon
} from '@heroicons/react/24/outline';

const UploadReport = () => {
  const [file, setFile] = useState(null);
  const [dragActive, setDragActive] = useState(false);
  const [uploading, setUploading] = useState(false);
  const [uploadedReport, setUploadedReport] = useState(null);
  const [analyzing, setAnalyzing] = useState(false);
  const [analysisResult, setAnalysisResult] = useState(null);
  const [query, setQuery] = useState('Summarize my blood test report');
  const navigate = useNavigate();

  const handleDrag = useCallback((e) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.type === "dragenter" || e.type === "dragover") {
      setDragActive(true);
    } else if (e.type === "dragleave") {
      setDragActive(false);
    }
  }, []);

  const handleDrop = useCallback((e) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(false);
    
    if (e.dataTransfer.files && e.dataTransfer.files[0]) {
      const droppedFile = e.dataTransfer.files[0];
      if (droppedFile.type === 'application/pdf') {
        setFile(droppedFile);
      } else {
        toast.error('Please upload a PDF file');
      }
    }
  }, []);

  const handleFileSelect = (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile) {
      if (selectedFile.type === 'application/pdf') {
        setFile(selectedFile);
      } else {
        toast.error('Please select a PDF file');
      }
    }
  };

  const removeFile = () => {
    setFile(null);
  };

  const handleUpload = async () => {
    if (!file) {
      toast.error('Please select a file to upload');
      return;
    }

    setUploading(true);
    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await axios.post('/api/upload-report', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      setUploadedReport(response.data);
      toast.success('Report uploaded successfully! Starting analysis...');
      
      // Automatically start analysis after upload
      await handleAnalyze(response.data.report_id);
    } catch (error) {
      console.error('Upload error:', error);
      toast.error(error.response?.data?.detail || 'Failed to upload report');
    } finally {
      setUploading(false);
    }
  };

  const handleAnalyze = async (reportId = null) => {
    const targetReportId = reportId || uploadedReport?.report_id;
    
    if (!targetReportId || !query.trim()) {
      toast.error('Please enter a query to analyze');
      return;
    }

    setAnalyzing(true);
    const formData = new FormData();
    formData.append('report_id', targetReportId);
    formData.append('query', query);

    try {
      const response = await axios.post('/api/analyze-report-sync', formData);
      setAnalysisResult(response.data.result);
      toast.success('Analysis completed successfully!');
      
      // Update the uploaded report with analysis status
      if (uploadedReport) {
        setUploadedReport({
          ...uploadedReport,
          processing_status: 'completed',
          analysis_result: response.data.result
        });
      }
    } catch (error) {
      console.error('Analysis error:', error);
      toast.error('Analysis failed: ' + (error.response?.data?.detail || 'Unknown error'));
    } finally {
      setAnalyzing(false);
    }
  };

  return (
    <div className="max-w-2xl mx-auto space-y-8">
      {/* Header */}
      <div className="flex items-center space-x-4">
        <button
          onClick={() => navigate('/dashboard')}
          className="p-2 text-medical-600 hover:text-medical-900 transition-colors"
        >
          <ArrowLeftIcon className="h-5 w-5" />
        </button>
        <div>
          <h1 className="text-3xl font-bold text-medical-900">Upload Blood Report</h1>
          <p className="mt-2 text-medical-600">
            Upload your blood test report PDF for AI analysis
          </p>
        </div>
      </div>

      {/* Upload Area */}
      <div className="bg-white rounded-xl shadow-sm border border-medical-200 p-8">
        {!file ? (
          <div
            className={`border-2 border-dashed rounded-lg p-8 text-center transition-colors ${
              dragActive 
                ? 'border-primary-400 bg-primary-50' 
                : 'border-medical-300 hover:border-medical-400'
            }`}
            onDragEnter={handleDrag}
            onDragLeave={handleDrag}
            onDragOver={handleDrag}
            onDrop={handleDrop}
          >
            <CloudArrowUpIcon className="mx-auto h-12 w-12 text-medical-400" />
            <div className="mt-4">
              <label htmlFor="file-upload" className="cursor-pointer">
                <span className="mt-2 block text-sm font-medium text-medical-900">
                  <span className="text-primary-600 hover:text-primary-500">
                    Click to upload
                  </span>{' '}
                  or drag and drop
                </span>
                <span className="mt-1 block text-xs text-medical-500">
                  PDF files only, max 10MB
                </span>
              </label>
              <input
                id="file-upload"
                name="file-upload"
                type="file"
                className="sr-only"
                accept=".pdf"
                onChange={handleFileSelect}
              />
            </div>
          </div>
        ) : (
          <div className="space-y-4">
            <div className="flex items-center space-x-4 p-4 bg-medical-50 rounded-lg border border-medical-200">
              <DocumentTextIcon className="h-8 w-8 text-primary-600" />
              <div className="flex-1 min-w-0">
                <p className="text-sm font-medium text-medical-900 truncate">
                  {file.name}
                </p>
                <p className="text-sm text-medical-500">
                  {(file.size / 1024 / 1024).toFixed(2)} MB
                </p>
              </div>
              <button
                onClick={removeFile}
                className="p-1 text-medical-400 hover:text-medical-600 transition-colors"
              >
                <XMarkIcon className="h-5 w-5" />
              </button>
            </div>
            
            <button
              onClick={handleUpload}
              disabled={uploading}
              className="w-full flex justify-center py-3 px-4 border border-transparent rounded-lg shadow-sm text-sm font-medium text-white bg-primary-600 hover:bg-primary-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
            >
              {uploading ? (
                <div className="flex items-center space-x-2">
                  <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white"></div>
                  <span>Uploading...</span>
                </div>
              ) : (
                'Upload Report'
              )}
            </button>
          </div>
        )}
      </div>

      {/* Instructions */}
      <div className="bg-blue-50 border border-blue-200 rounded-lg p-6">
        <h3 className="text-lg font-medium text-blue-900 mb-3">
          Upload Guidelines
        </h3>
        <ul className="space-y-2 text-sm text-blue-800">
          <li className="flex items-start">
            <span className="inline-block w-2 h-2 bg-blue-400 rounded-full mt-2 mr-3 flex-shrink-0"></span>
            Only PDF files are accepted
          </li>
          <li className="flex items-start">
            <span className="inline-block w-2 h-2 bg-blue-400 rounded-full mt-2 mr-3 flex-shrink-0"></span>
            Maximum file size: 10MB
          </li>
          <li className="flex items-start">
            <span className="inline-block w-2 h-2 bg-blue-400 rounded-full mt-2 mr-3 flex-shrink-0"></span>
            Ensure the PDF contains clear, readable text
          </li>
          <li className="flex items-start">
            <span className="inline-block w-2 h-2 bg-blue-400 rounded-full mt-2 mr-3 flex-shrink-0"></span>
            Processing may take 1-3 minutes depending on file size
          </li>
        </ul>
      </div>

      {/* Analysis Section - Show after upload */}
      {uploadedReport && (
        <div className="bg-white rounded-xl shadow-sm border border-medical-200 p-6">
          <h2 className="text-lg font-semibold text-medical-900 mb-4">Analyze Your Report</h2>
          
          <div className="space-y-4">
            <div>
              <label htmlFor="analysis-query" className="block text-sm font-medium text-medical-700 mb-2">
                What would you like to know about your report?
              </label>
              <textarea
                id="analysis-query"
                value={query}
                onChange={(e) => setQuery(e.target.value)}
                placeholder="e.g., Summarize my blood test report, Are there any concerning values?, What do these results mean?"
                className="w-full px-3 py-3 border border-medical-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-primary-500 focus:border-primary-500 resize-none"
                rows="3"
              />
            </div>
            
            <div className="flex space-x-3">
              <button
                onClick={() => handleAnalyze()}
                disabled={analyzing || !query.trim()}
                className="flex items-center space-x-2 px-4 py-2 bg-primary-600 text-white rounded-lg hover:bg-primary-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
              >
                {analyzing ? (
                  <>
                    <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white"></div>
                    <span>Analyzing...</span>
                  </>
                ) : (
                  <>
                    <MagnifyingGlassIcon className="h-4 w-4" />
                    <span>Analyze Report</span>
                  </>
                )}
              </button>
              
              <button
                onClick={() => navigate('/dashboard')}
                className="px-4 py-2 border border-medical-300 text-medical-700 rounded-lg hover:bg-medical-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500 transition-colors"
              >
                Go to Dashboard
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Analysis Results */}
      {analysisResult && (
        <div className="bg-white rounded-xl shadow-sm border border-medical-200 p-6">
          <div className="flex items-center justify-between mb-4">
            <h2 className="text-xl font-semibold text-medical-900">
              Analysis Results
            </h2>
            <div className="flex space-x-3">
              <button
                onClick={() => handleAnalyze()}
                disabled={analyzing || !query.trim()}
                className="inline-flex items-center px-3 py-1.5 border border-medical-300 shadow-sm text-sm font-medium rounded-md text-medical-700 bg-white hover:bg-medical-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
              >
                <MagnifyingGlassIcon className="h-4 w-4 mr-1" />
                Re-analyze
              </button>
              <button
                onClick={() => navigate('/dashboard')}
                className="inline-flex items-center px-3 py-1.5 border border-transparent shadow-sm text-sm font-medium rounded-md text-white bg-primary-600 hover:bg-primary-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500 transition-colors"
              >
                View in Dashboard
              </button>
            </div>
          </div>
          
          <div className="bg-green-50 border border-green-200 rounded-lg p-4 mb-4">
            <div className="flex items-center">
              <div className="flex-shrink-0">
                <svg className="h-5 w-5 text-green-400" viewBox="0 0 20 20" fill="currentColor">
                  <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zm3.707-9.293a1 1 0 00-1.414-1.414L9 10.586 7.707 9.293a1 1 0 00-1.414 1.414l2 2a1 1 0 001.414 0l4-4z" clipRule="evenodd" />
                </svg>
              </div>
              <div className="ml-3">
                <p className="text-sm font-medium text-green-800">
                  Analysis completed successfully!
                </p>
                <p className="text-sm text-green-700 mt-1">
                  Your blood test report has been analyzed and the results are ready.
                </p>
              </div>
            </div>
          </div>
          
          <div className="bg-medical-50 border border-medical-200 rounded-lg p-4">
            <h3 className="text-lg font-medium text-medical-900 mb-3">Analysis Summary</h3>
            <div className="prose prose-medical max-w-none">
              <div className="whitespace-pre-wrap text-medical-800 leading-relaxed">
                {analysisResult}
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default UploadReport;
