import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { toast } from 'react-toastify';
import axios from 'axios';
import { 
  ArrowLeftIcon,
  DocumentTextIcon,
  ClockIcon,
  CheckCircleIcon,
  ExclamationTriangleIcon,
  MagnifyingGlassIcon,
  ChartBarIcon,
  CalendarIcon
} from '@heroicons/react/24/outline';

const ReportDetails = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [report, setReport] = useState(null);
  const [loading, setLoading] = useState(true);
  const [query, setQuery] = useState('');
  const [analyzing, setAnalyzing] = useState(false);
  const [taskId, setTaskId] = useState(null);
  const [analysisResult, setAnalysisResult] = useState(null);

  useEffect(() => {
    fetchReportDetails();
  }, [id]);

  useEffect(() => {
    if (taskId) {
      const interval = setInterval(checkTaskStatus, 2000);
      return () => clearInterval(interval);
    }
  }, [taskId]);

  const fetchReportDetails = async () => {
    try {
      const response = await axios.get(`/api/report/${id}`);
      setReport(response.data);
      if (response.data.analysis_result) {
        setAnalysisResult(response.data.analysis_result);
      }
    } catch (error) {
      toast.error('Failed to fetch report details');
      console.error('Error fetching report:', error);
    } finally {
      setLoading(false);
    }
  };

  const checkTaskStatus = async () => {
    try {
      const response = await axios.get(`/api/task-status/${taskId}`);
      
      if (response.data.status === 'completed') {
        setAnalysisResult(response.data.result.result);
        setTaskId(null);
        setAnalyzing(false);
        toast.success('Analysis completed!');
        fetchReportDetails(); // Refresh report data
      } else if (response.data.status === 'failed') {
        setTaskId(null);
        setAnalyzing(false);
        toast.error('Analysis failed: ' + response.data.error);
      }
    } catch (error) {
      console.error('Error checking task status:', error);
    }
  };

  const handleAnalyze = async () => {
    if (!query.trim()) {
      toast.error('Please enter a query');
      return;
    }

    setAnalyzing(true);
    const formData = new FormData();
    formData.append('report_id', id);
    formData.append('query', query);

    try {
      const response = await axios.post('/api/analyze-report', formData);
      setTaskId(response.data.task_id);
      toast.info('Analysis started. This may take a few minutes...');
    } catch (error) {
      setAnalyzing(false);
      toast.error('Failed to start analysis');
      console.error('Error starting analysis:', error);
    }
  };

  const getStatusIcon = (status) => {
    switch (status) {
      case 'completed':
        return <CheckCircleIcon className="h-5 w-5 text-green-500" />;
      case 'processing':
        return <ClockIcon className="h-5 w-5 text-yellow-500 animate-pulse" />;
      case 'failed':
        return <ExclamationTriangleIcon className="h-5 w-5 text-red-500" />;
      default:
        return <ClockIcon className="h-5 w-5 text-gray-400" />;
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'completed':
        return 'bg-green-100 text-green-800';
      case 'processing':
        return 'bg-yellow-100 text-yellow-800';
      case 'failed':
        return 'bg-red-100 text-red-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-600"></div>
      </div>
    );
  }

  if (!report) {
    return (
      <div className="text-center py-12">
        <p className="text-medical-600">Report not found</p>
      </div>
    );
  }

  return (
    <div className="max-w-4xl mx-auto space-y-8">
      {/* Header */}
      <div className="flex items-center space-x-4">
        <button
          onClick={() => navigate('/dashboard')}
          className="p-2 text-medical-600 hover:text-medical-900 transition-colors"
        >
          <ArrowLeftIcon className="h-5 w-5" />
        </button>
        <div>
          <h1 className="text-3xl font-bold text-medical-900">Report Details</h1>
          <p className="mt-2 text-medical-600">
            {report.filename}
          </p>
        </div>
      </div>

      {/* Report Info */}
      <div className="bg-white rounded-xl shadow-sm border border-medical-200 p-6">
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          <div className="flex items-center space-x-3">
            <DocumentTextIcon className="h-6 w-6 text-primary-600" />
            <div>
              <p className="text-sm font-medium text-medical-600">Status</p>
              <div className="flex items-center space-x-2">
                {getStatusIcon(report.processing_status)}
                <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${getStatusColor(report.processing_status)}`}>
                  {report.processing_status}
                </span>
              </div>
            </div>
          </div>

          <div className="flex items-center space-x-3">
            <CalendarIcon className="h-6 w-6 text-primary-600" />
            <div>
              <p className="text-sm font-medium text-medical-600">Upload Date</p>
              <p className="text-sm text-medical-900">{formatDate(report.upload_date)}</p>
            </div>
          </div>

          {report.confidence_score && (
            <div className="flex items-center space-x-3">
              <ChartBarIcon className="h-6 w-6 text-primary-600" />
              <div>
                <p className="text-sm font-medium text-medical-600">Confidence</p>
                <p className="text-sm text-medical-900">{Math.round(report.confidence_score * 100)}%</p>
              </div>
            </div>
          )}
        </div>
      </div>

      {/* Query Section */}
      <div className="bg-white rounded-xl shadow-sm border border-medical-200 p-6">
        <h2 className="text-lg font-semibold text-medical-900 mb-4">Ask Questions About Your Report</h2>
        
        <div className="space-y-4">
          <div>
            <label htmlFor="query" className="block text-sm font-medium text-medical-700 mb-2">
              Your Question
            </label>
            <textarea
              id="query"
              value={query}
              onChange={(e) => setQuery(e.target.value)}
              placeholder="e.g., What are the key findings in my blood test? Are there any concerning values?"
              className="w-full px-3 py-3 border border-medical-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-primary-500 focus:border-primary-500 resize-none"
              rows="3"
            />
          </div>
          
          <button
            onClick={handleAnalyze}
            disabled={analyzing || !query.trim()}
            className="flex items-center space-x-2 px-4 py-2 bg-primary-600 text-white rounded-lg hover:bg-primary-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
          >
            {analyzing ? (
              <>
                <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white"></div>
                <span>Analyzing...</span>
              </>
            ) : (
              <>
                <MagnifyingGlassIcon className="h-4 w-4" />
                <span>Analyze Report</span>
              </>
            )}
          </button>
        </div>
      </div>

      {/* Analysis Results */}
      {analysisResult && (
        <div className="bg-white rounded-xl shadow-sm border border-medical-200 p-6">
          <h2 className="text-lg font-semibold text-medical-900 mb-4">Analysis Results</h2>
          <div className="bg-medical-50 rounded-lg p-4">
            <div className="prose prose-sm max-w-none text-medical-800">
              {analysisResult.split('\n').map((line, index) => (
                <p key={index} className="mb-2">
                  {line}
                </p>
              ))}
            </div>
          </div>
        </div>
      )}

      {/* Extracted Text */}
      {report.extracted_text && (
        <div className="bg-white rounded-xl shadow-sm border border-medical-200 p-6">
          <h2 className="text-lg font-semibold text-medical-900 mb-4">Extracted Text</h2>
          <div className="bg-medical-50 rounded-lg p-4 max-h-64 overflow-y-auto">
            <pre className="text-sm text-medical-700 whitespace-pre-wrap font-sans">
              {report.extracted_text}
            </pre>
          </div>
        </div>
      )}
    </div>
  );
};

export default ReportDetails;
