import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { toast } from 'react-toastify';
import { 
  DocumentTextIcon,
  ClockIcon,
  CheckCircleIcon,
  ExclamationTriangleIcon,
  PlusIcon,
  ChartBarIcon,
  CalendarIcon
} from '@heroicons/react/24/outline';

const Dashboard = () => {
  const [reports, setReports] = useState([]);
  const [loading, setLoading] = useState(true);
  const [stats, setStats] = useState({
    total: 0,
    completed: 0,
    processing: 0,
    failed: 0
  });

  useEffect(() => {
    fetchReports();
  }, []);

  const fetchReports = async () => {
    try {
      const response = await axios.get('/api/user-reports');
      setReports(response.data);
      
      // Calculate stats
      const stats = {
        total: response.data.length,
        completed: response.data.filter(r => r.processing_status === 'completed').length,
        processing: response.data.filter(r => r.processing_status === 'processing').length,
        failed: response.data.filter(r => r.processing_status === 'failed').length
      };
      setStats(stats);
    } catch (error) {
      toast.error('Failed to fetch reports');
      console.error('Error fetching reports:', error);
    } finally {
      setLoading(false);
    }
  };

  const getStatusIcon = (status) => {
    switch (status) {
      case 'completed':
        return <CheckCircleIcon className="h-5 w-5 text-green-500" />;
      case 'processing':
        return <ClockIcon className="h-5 w-5 text-yellow-500 animate-pulse" />;
      case 'failed':
        return <ExclamationTriangleIcon className="h-5 w-5 text-red-500" />;
      default:
        return <ClockIcon className="h-5 w-5 text-gray-400" />;
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'completed':
        return 'bg-green-100 text-green-800';
      case 'processing':
        return 'bg-yellow-100 text-yellow-800';
      case 'failed':
        return 'bg-red-100 text-red-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  // Pie Chart Component
  const PieChart = ({ data, size = 200 }) => {
    const radius = size / 2;
    const center = radius;
    
    // Calculate total and percentages
    const total = data.reduce((sum, item) => sum + item.value, 0);
    let currentAngle = 0;
    
    const segments = data.map((item, index) => {
      const percentage = total > 0 ? item.value / total : 0;
      const angle = percentage * 360;
      const startAngle = currentAngle;
      currentAngle += angle;
      
      // Calculate SVG arc parameters
      const startRadians = (startAngle - 90) * (Math.PI / 180);
      const endRadians = (currentAngle - 90) * (Math.PI / 180);
      
      const x1 = center + radius * Math.cos(startRadians);
      const y1 = center + radius * Math.sin(startRadians);
      const x2 = center + radius * Math.cos(endRadians);
      const y2 = center + radius * Math.sin(endRadians);
      
      const largeArcFlag = angle > 180 ? 1 : 0;
      
      const pathData = [
        `M ${center} ${center}`,
        `L ${x1} ${y1}`,
        `A ${radius} ${radius} 0 ${largeArcFlag} 1 ${x2} ${y2}`,
        'Z'
      ].join(' ');
      
      return {
        ...item,
        pathData,
        percentage: Math.round(percentage * 100),
        centerAngle: startAngle + angle / 2
      };
    });

    return (
      <div className="relative">
        <svg width={size} height={size} className="mx-auto">
          {segments.map((segment, index) => (
            <g key={index}>
              <path
                d={segment.pathData}
                fill={segment.color}
                stroke="white"
                strokeWidth="2"
                className="transition-all duration-300 hover:opacity-80"
              />
              {/* Percentage labels */}
              {segment.percentage > 5 && (
                <text
                  x={center + (radius * 0.7) * Math.cos((segment.centerAngle - 90) * (Math.PI / 180))}
                  y={center + (radius * 0.7) * Math.sin((segment.centerAngle - 90) * (Math.PI / 180))}
                  textAnchor="middle"
                  dominantBaseline="middle"
                  className="text-xs font-semibold fill-white pointer-events-none"
                >
                  {segment.percentage}%
                </text>
              )}
            </g>
          ))}
        </svg>
        
        {/* Center label */}
        <div className="absolute inset-0 flex items-center justify-center">
          <div className="text-center">
            <div className="text-2xl font-bold text-medical-900">{stats.total}</div>
            <div className="text-xs text-medical-600">Total</div>
          </div>
        </div>
      </div>
    );
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-600"></div>
      </div>
    );
  }

  return (
    <div className="space-y-8">
      {/* Header */}
      <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between">
        <div>
          <h1 className="text-3xl font-bold text-medical-900">Dashboard</h1>
          <p className="mt-2 text-medical-600">
            Manage your blood test reports and view analysis results
          </p>
        </div>
        <Link
          to="/upload"
          className="mt-4 sm:mt-0 inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-lg text-white bg-primary-600 hover:bg-primary-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500 transition-colors"
        >
          <PlusIcon className="h-4 w-4 mr-2" />
          Upload New Report
        </Link>
      </div>

      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        <div className="bg-white p-6 rounded-xl shadow-sm border border-medical-200">
          <div className="flex items-center">
            <div className="flex-shrink-0">
              <DocumentTextIcon className="h-8 w-8 text-primary-600" />
            </div>
            <div className="ml-4">
              <p className="text-sm font-medium text-medical-600">Total Reports</p>
              <p className="text-2xl font-bold text-medical-900">{stats.total}</p>
            </div>
          </div>
        </div>

        <div className="bg-white p-6 rounded-xl shadow-sm border border-medical-200">
          <div className="flex items-center">
            <div className="flex-shrink-0">
              <CheckCircleIcon className="h-8 w-8 text-green-600" />
            </div>
            <div className="ml-4">
              <p className="text-sm font-medium text-medical-600">Completed</p>
              <p className="text-2xl font-bold text-medical-900">{stats.completed}</p>
            </div>
          </div>
        </div>

        <div className="bg-white p-6 rounded-xl shadow-sm border border-medical-200">
          <div className="flex items-center">
            <div className="flex-shrink-0">
              <ClockIcon className="h-8 w-8 text-yellow-600" />
            </div>
            <div className="ml-4">
              <p className="text-sm font-medium text-medical-600">Processing</p>
              <p className="text-2xl font-bold text-medical-900">{stats.processing}</p>
            </div>
          </div>
        </div>

        <div className="bg-white p-6 rounded-xl shadow-sm border border-medical-200">
          <div className="flex items-center">
            <div className="flex-shrink-0">
              <ExclamationTriangleIcon className="h-8 w-8 text-red-600" />
            </div>
            <div className="ml-4">
              <p className="text-sm font-medium text-medical-600">Failed</p>
              <p className="text-2xl font-bold text-medical-900">{stats.failed}</p>
            </div>
          </div>
        </div>
      </div>

      {/* Pie Chart Section */}
      {stats.total > 0 && (
        <div className="bg-white rounded-xl shadow-sm border border-medical-200 p-6">
          <h2 className="text-lg font-semibold text-medical-900 mb-6 text-center">
            Report Status Distribution
          </h2>
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-8 items-center">
            <div className="flex justify-center">
              <PieChart
                data={[
                  { label: 'Completed', value: stats.completed, color: '#10B981' },
                  { label: 'Processing', value: stats.processing, color: '#F59E0B' },
                  { label: 'Failed', value: stats.failed, color: '#EF4444' },
                  { label: 'Pending', value: stats.total - stats.completed - stats.processing - stats.failed, color: '#6B7280' }
                ].filter(item => item.value > 0)}
                size={240}
              />
            </div>
            <div className="space-y-4">
              <div className="grid grid-cols-2 gap-4">
                <div className="flex items-center space-x-3 p-3 bg-green-50 rounded-lg">
                  <div className="w-4 h-4 bg-green-500 rounded-full"></div>
                  <div>
                    <p className="text-sm font-medium text-green-900">Completed</p>
                    <p className="text-xs text-green-600">{stats.completed} reports</p>
                  </div>
                </div>
                <div className="flex items-center space-x-3 p-3 bg-yellow-50 rounded-lg">
                  <div className="w-4 h-4 bg-yellow-500 rounded-full"></div>
                  <div>
                    <p className="text-sm font-medium text-yellow-900">Processing</p>
                    <p className="text-xs text-yellow-600">{stats.processing} reports</p>
                  </div>
                </div>
                <div className="flex items-center space-x-3 p-3 bg-red-50 rounded-lg">
                  <div className="w-4 h-4 bg-red-500 rounded-full"></div>
                  <div>
                    <p className="text-sm font-medium text-red-900">Failed</p>
                    <p className="text-xs text-red-600">{stats.failed} reports</p>
                  </div>
                </div>
                <div className="flex items-center space-x-3 p-3 bg-gray-50 rounded-lg">
                  <div className="w-4 h-4 bg-gray-500 rounded-full"></div>
                  <div>
                    <p className="text-sm font-medium text-gray-900">Pending</p>
                    <p className="text-xs text-gray-600">{stats.total - stats.completed - stats.processing - stats.failed} reports</p>
                  </div>
                </div>
              </div>
              <div className="text-center">
                <p className="text-sm text-medical-600">
                  Success Rate: <span className="font-semibold text-green-600">
                    {stats.total > 0 ? Math.round((stats.completed / stats.total) * 100) : 0}%
                  </span>
                </p>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Reports List */}
      <div className="bg-white rounded-xl shadow-sm border border-medical-200">
        <div className="px-6 py-4 border-b border-medical-200">
          <h2 className="text-lg font-semibold text-medical-900">Recent Reports</h2>
        </div>
        
        {reports.length === 0 ? (
          <div className="p-8 text-center">
            <DocumentTextIcon className="mx-auto h-12 w-12 text-medical-400" />
            <h3 className="mt-2 text-sm font-medium text-medical-900">No reports yet</h3>
            <p className="mt-1 text-sm text-medical-500">
              Get started by uploading your first blood test report.
            </p>
            <div className="mt-6">
              <Link
                to="/upload"
                className="inline-flex items-center px-4 py-2 border border-transparent shadow-sm text-sm font-medium rounded-lg text-white bg-primary-600 hover:bg-primary-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500 transition-colors"
              >
                <PlusIcon className="h-4 w-4 mr-2" />
                Upload Report
              </Link>
            </div>
          </div>
        ) : (
          <div className="overflow-hidden">
            <ul className="divide-y divide-medical-200">
              {reports.map((report) => (
                <li key={report.id} className="px-6 py-4 hover:bg-medical-50 transition-colors">
                  <div className="flex items-center justify-between">
                    <div className="flex items-center space-x-4">
                      <div className="flex-shrink-0">
                        {getStatusIcon(report.processing_status)}
                      </div>
                      <div className="flex-1 min-w-0">
                        <p className="text-sm font-medium text-medical-900 truncate">
                          {report.filename}
                        </p>
                        <div className="flex items-center space-x-4 mt-1">
                          <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-medical-100 text-medical-800">
                            <CalendarIcon className="h-3 w-3 mr-1" />
                            {formatDate(report.upload_date)}
                          </span>
                          <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${getStatusColor(report.processing_status)}`}>
                            {report.processing_status}
                          </span>
                          {report.confidence_score && (
                            <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-blue-100 text-blue-800">
                              <ChartBarIcon className="h-3 w-3 mr-1" />
                              {Math.round(report.confidence_score * 100)}% confidence
                            </span>
                          )}
                        </div>
                      </div>
                    </div>
                    <div className="flex items-center space-x-2">
                      <Link
                        to={`/report/${report.id}`}
                        className="inline-flex items-center px-3 py-1.5 border border-medical-300 shadow-sm text-xs font-medium rounded-md text-medical-700 bg-white hover:bg-medical-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500 transition-colors"
                      >
                        View Details
                      </Link>
                    </div>
                  </div>
                </li>
              ))}
            </ul>
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
